INSERT INTO `posts` (`id`, `title`, `slug`, `body`, `published`, `trending`, `image`, `user_id`, `like`, `dislike`, `view`, `request`, `requested_at`, `created_at`, `updated_at`) VALUES
(1, 'Giáng Son: \"Nhạc sĩ Nguyễn Trọng Tạo đã giấu đi nỗi đau bằng bản lĩnh của mình\"', 'giang-son-nhac-si-nguyen-trong-tao-da-giau-di-noi-dau-bang-ban-linh-cua-minh', '<h3><strong>(D&acirc;n Việt)</strong> Với nhạc sĩ Gi&aacute;ng Son, nhạc sĩ Nguyễn Trọng Tạo l&agrave; người v&ocirc; c&ugrave;ng đặc biệt, l&agrave; cha đẻ của ban nhạc 5 d&ograve;ng kẻ v&agrave; l&agrave; người n&acirc;ng đỡ cho ban nhạc rất nhiều ở những năm đầu ti&ecirc;n.</h3>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<ul>\r\n	<li>\r\n	<h2><a href=\"http://danviet.vn/van-hoa/trong-tan-bat-ngo-voi-cuoc-dien-thoai-dem-cua-nhac-si-nguyen-trong-tao-946241.html\">Trọng Tấn bất ngờ với cuộc điện thoại đ&ecirc;m của nhạc sĩ Nguyễn Trọng Tạo</a></h2>\r\n	</li>\r\n	<li>\r\n	<h2><a href=\"http://danviet.vn/van-hoa/cac-ca-si-nghen-ngao-dau-don-khi-nhac-si-nguyen-trong-tao-qua-doi-946189.html\">C&aacute;c ca sĩ nghẹn ng&agrave;o đau đớn khi nhạc sĩ Nguyễn Trọng Tạo qua đời</a></h2>\r\n	</li>\r\n	<li>\r\n	<h2><a href=\"http://danviet.vn/van-hoa/nguyen-trong-tao-noi-nho-rong-choi-945956.html\">Nguyễn Trọng Tạo: &ldquo;Nỗi nhớ rong chơi&rdquo;!</a></h2>\r\n	</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"http://streaming1.danviet.vn/images/2014/share-fb.gif\" style=\"height:20px; width:67px\" />&nbsp;<img alt=\"\" src=\"http://streaming1.danviet.vn/images/2014/share-gg.gif\" style=\"height:20px; width:67px\" /><img alt=\"giang son: &quot;nhac si nguyen trong tao da giau di noi dau bang ban linh cua minh&quot; hinh anh 1\" src=\"http://streaming1.danviet.vn/upload/1-2019/images/2019-01-09/co1_feyi-1547012865-width665height911.jpg\" style=\"height:822px; width:600px\" /></p>\r\n\r\n<p>Nh&oacute;m 5 d&ograve;ng kẻ</p>\r\n\r\n<p>Sau khi đ&atilde; trở n&ecirc;n th&acirc;n thiết, với nữ nhạc sĩ, &ocirc;ng c&ograve;n l&agrave; một người anh lớn c&oacute; chung quan điểm về thẩm mỹ nghệ thuật. Mỗi khi viết xong một tập thơ n&agrave;o, nhạc sĩ đều tặng cho Gi&aacute;ng Son v&agrave; c&ugrave;ng nhau chia sẻ cảm x&uacute;c. &quot;Cỏ v&agrave; mưa&quot; l&agrave; một ca kh&uacute;c nổi tiếng của nhạc sĩ Gi&aacute;ng Son phổ nhạc từ thơ của nhạc sĩ Nguyễn Trọng Tạo cũng ra đời theo c&aacute;ch như vậy. Ngo&agrave;i ra c&ograve;n c&oacute; &quot;C&acirc;y &aacute;nh s&aacute;ng&quot;, &quot;C&otilde;i nhớ&quot;&hellip;, nhưng&nbsp;&quot;Cỏ v&agrave; mưa&quot; vẫn l&agrave; ca kh&uacute;c được biết đến nhiều nhất với sự kết hợp của hai t&acirc;m hồn đồng điệu.</p>\r\n\r\n<p><img alt=\"\" src=\"http://streaming1.danviet.vn/images/2014/share-fb.gif\" style=\"height:20px; width:67px\" />&nbsp;<img alt=\"\" src=\"http://streaming1.danviet.vn/images/2014/share-gg.gif\" style=\"height:20px; width:67px\" /><img alt=\"giang son: &quot;nhac si nguyen trong tao da giau di noi dau bang ban linh cua minh&quot; hinh anh 2\" src=\"http://streaming1.danviet.vn/upload/1-2019/images/2019-01-09/3_204119-1547012905-width700height464.jpg\" style=\"height:398px; width:600px\" /></p>\r\n\r\n<p>Nhạc sĩ Gi&aacute;ng Son trong liveshow của nhạc sĩ Nguyễn Trọng Tạo</p>\r\n\r\n<p>Nhạc sĩ Gi&aacute;ng Son kể rằng c&ocirc; rất biết ơn nhạc sĩ Nguyễn Trọng Tạo kh&ocirc;ng chỉ trong nghệ thuật v&agrave; c&ograve;n ở cuộc sống. &ldquo;Anh Tạo rất t&igrave;nh cảm, l&uacute;c n&agrave;o cũng l&agrave; một người anh lớn, chơi đẹp, h&agrave;o sảng. Năm ngo&aacute;i khi bố t&ocirc;i qua đời, anh Tạo đ&atilde; nhanh ch&oacute;ng chia sẻ tr&ecirc;n facebook cho mọi người được biết khiến t&ocirc;i rất cảm động&rdquo;.</p>\r\n\r\n<p>Cả hai liveshow của nhạc sĩ Nguyễn Trọng Tạo ở Nh&agrave; h&aacute;t lớn H&agrave; Nội&nbsp;v&agrave; ở Vinh, cố nhạc sĩ đều mời nhạc sĩ Gi&aacute;ng Son l&agrave;m kh&aacute;ch mời v&agrave; muốn c&ocirc; c&oacute; những chia sẻ trong liveshow. Đ&oacute; l&agrave; một vinh dự rất lớn với Gi&aacute;ng Son v&igrave; c&ocirc; nghĩ cố nhạc sĩ th&acirc;n với rất nhiều nghệ sĩ kh&aacute;c.</p>\r\n\r\n<p><img alt=\"\" src=\"http://streaming1.danviet.vn/images/2014/share-fb.gif\" style=\"height:20px; width:67px\" />&nbsp;<img alt=\"\" src=\"http://streaming1.danviet.vn/images/2014/share-gg.gif\" style=\"height:20px; width:67px\" /><img alt=\"giang son: &quot;nhac si nguyen trong tao da giau di noi dau bang ban linh cua minh&quot; hinh anh 3\" src=\"http://streaming1.danviet.vn/upload/1-2019/images/2019-01-09/co3_zuie-1547012929-width665height696.jpg\" style=\"height:628px; width:600px\" /></p>\r\n\r\n<p>Nhạc sĩ Gi&aacute;ng Son v&agrave; nhạc sĩ Nguyễn Trọng Tạo</p>\r\n\r\n<p>Nhạc sĩ Gi&aacute;ng Son rất thương bậc đ&agrave;n anh của m&igrave;nh về cuối đời mắc phải căn bệnh hiểm ngh&egrave;o, chắc chắn cố nhạc sĩ rất đau đớn, v&agrave; phải giấu đi sự đau đớn ấy bằng bản lĩnh của m&igrave;nh. Mỗi lần gặp bạn b&egrave;, &ocirc;ng lu&ocirc;n tươi cười vui vẻ, kh&ocirc;ng muốn để mọi người thương x&oacute;t cho m&igrave;nh.</p>\r\n\r\n<p>Chơi với nhau qu&aacute; l&acirc;u, kỷ niệm với nhau qu&aacute; nhiều, sự ra đi của cố nhạc sĩ d&ugrave; c&oacute; thể đo&aacute;n trước được nhưng với Gi&aacute;ng Son đ&acirc;y vẫn l&agrave; sự hụt hẫng kh&ocirc;ng thể lấp đầy. Đ&ecirc;m 7.1 c&ocirc; kh&ocirc;ng thể ngủ được, buổi tối h&ocirc;m ấy khi c&ocirc; gọi điện cho con g&aacute;i cố nhạc sĩ th&igrave; đươc b&aacute;o tin &ocirc;ng c&oacute; đỡ hơn v&agrave; c&oacute; vẻ tiến triển, nhưng đến 19h20 lại nghe tin &ocirc;ng qua đời.&nbsp;Số tuổi 72 của nhạc sĩ chưa phải l&agrave; cao trong khi &ocirc;ng vẫn tr&agrave;n đầy nhiệt huyết. Những g&igrave; nhạc sĩ l&agrave;m cho thế hệ trẻ sẽ kh&ocirc;ng bao giờ bị l&atilde;ng qu&ecirc;n, v&agrave; sự ra đi của &ocirc;ng để lại một khoảng trống kh&oacute; c&oacute; thể lấp đầy.</p>', 1, '2019-01-03 00:00:00', '1547045889.png', 3, 8, 8, 33, 0, NULL, '2018-11-01 07:14:39', '2019-01-09 23:31:46'),
